import React, { useEffect, useState } from 'react';
import axios from 'axios';
import SearchBar from './components/SearchBar';
import ProductList from './components/ProductList';
import Filters from './components/Filters';
import Chat from './components/Chat';
import './styles/App.css';

const exchangeRates = {
  QAR: 0.2740, // Qatari Riyal
  KWD: 3.2415, // Kuwaiti Dinar
  OMR: 2.6000, // Omani Rial
  AED: 0.2723, // UAE Dirham
  SAR: 0.2666, // Saudi Riyal
  BHD: 2.6596, // Bahraini Dinar
  USD: 1.0000, // US Dollar
};

function convertToUSD(price, currency) {
  const rate = exchangeRates[currency];
  return rate ? price * rate : price; // Convert price to USD or return original if no rate found
}

function App() {
  const [allProducts, setAllProducts] = useState([]); // Store all fetched products
  const [filteredProducts, setFilteredProducts] = useState([]); // Products after applying filters
  const [filters, setFilters] = useState({
    priceMin: null,
    priceMax: null,
    category: '',
    size: '',
  });

  // Fetch products when the page loads
  useEffect(() => {
    fetchProducts('');
  }, []);

  const fetchProducts = async (query) => {
    try {
        const response = await axios.get(`http://127.0.0.1:8000/api/search?query=${query}`);
        const convertedProducts = response.data.hits.map((product) => ({
        ...product,
        current_price_usd: convertToUSD(product.current_price, product.currency), // Add USD prices
        old_price_usd: product.old_price
            ? convertToUSD(product.old_price, product.currency)
            : null,
        }));
        setAllProducts(convertedProducts); // Store all products
        setFilteredProducts(convertedProducts); // Display products initially
    } catch (error) {
        console.error('Error fetching products:', error);
    }
    };


  const handleSearch = (query) => {
    fetchProducts(query); // Fetch new products based on search query
  };

  const applyFilters = (newFilters) => {
    setFilters(newFilters);
    applyFiltersToProducts(allProducts, newFilters); // Apply filters to existing products
  };

  const applyFiltersToProducts = (products, activeFilters) => {
    let results = products;

    if (activeFilters.priceMin) {
      results = results.filter(
        (product) => product.current_price_usd >= activeFilters.priceMin
      );
    }

    if (activeFilters.priceMax) {
      results = results.filter(
        (product) => product.current_price_usd <= activeFilters.priceMax
      );
    }

    if (activeFilters.category) {
      results = results.filter((product) => product.category_name === activeFilters.category);
    }

    if (activeFilters.size) {
      results = results.filter((product) => product.sizes.includes(activeFilters.size));
    }

    setFilteredProducts(results); // Update the filtered product list
  };

  return (
    <div className="app-container">
      <div className="search-section">
        <SearchBar onSearch={handleSearch} />
      </div>
      <div className="filters-section">
        <Filters filters={filters} onApplyFilters={applyFilters} />
      </div>
      <div className="results-section">
        <ProductList products={filteredProducts} />
      </div>
      <div className="chat-section">
        <Chat onQuery={handleSearch} />
      </div>
    </div>
  );
}

export default App;
