import React, { useEffect, useState } from 'react';
import axios from 'axios';
import SearchBar from './components/SearchBar';
import ProductList from './components/ProductList';
import Filters from './components/Filters';
import Chat from './components/Chat';
import './styles/App.css';

function App() {
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [filters, setFilters] = useState({
    priceMin: null,
    priceMax: null,
    category: '',
    size: '',
  });

  // Fetch products when the page loads
  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/api/search?query=');
      setFilteredProducts(response.data.hits);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleSearch = async (query) => {
    try {
      const response = await axios.get(`http://127.0.0.1:8000/api/search?query=${query}`);
      setFilteredProducts(response.data.hits);
    } catch (error) {
      console.error('Error searching products:', error);
    }
  };

  const applyFilters = (newFilters) => {
    setFilters(newFilters);

    // Filter products based on the current filters
    let results = filteredProducts;

    if (newFilters.priceMin) {
      results = results.filter((product) => product.current_price >= newFilters.priceMin);
    }

    if (newFilters.priceMax) {
      results = results.filter((product) => product.current_price <= newFilters.priceMax);
    }

    if (newFilters.category) {
      results = results.filter((product) => product.category_name === newFilters.category);
    }

    if (newFilters.size) {
      results = results.filter((product) => product.sizes.includes(newFilters.size));
    }

    setFilteredProducts(results);
  };

  return (
    <div className="app-container">
      <div className="search-section">
        <SearchBar onSearch={handleSearch} />
      </div>
      <div className="filters-section">
        <Filters filters={filters} onApplyFilters={applyFilters} />
      </div>
      <div className="results-section">
        <ProductList products={filteredProducts} />
      </div>
      <div className="chat-section">
        <Chat onQuery={handleSearch} />
      </div>
    </div>
  );
}

export default App;
