import React, { useState } from 'react';
import productsData from './data/products_1.json';
import SearchBar from './components/SearchBar';
import Filters from './components/Filters';
import ProductList from './components/ProductList';
import './styles/App.css';

function App() {
  const [query, setQuery] = useState('');
  const [filteredProducts, setFilteredProducts] = useState(productsData);
  const [filters, setFilters] = useState({
    price: null,
    category: null,
    rating: null,
    gender: null,
    size: null,
  });

  const handleSearch = (searchQuery) => {
    setQuery(searchQuery); // Save the search query in state

    // Start filtering from the original data
    const results = productsData.filter(
      (product) =>
        product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (product.description && product.description.toLowerCase().includes(searchQuery.toLowerCase()))
    );

    setFilteredProducts(results); // Update the filtered results
  };

  const applyFilters = (newFilters) => {
    setFilters(newFilters); // Save the applied filters in state

    // Start filtering from the original data
    let results = productsData;

    // Apply filters
    if (newFilters.price) {
      results = results.filter((product) => product.current_price <= newFilters.price);
    }
    if (newFilters.category) {
      results = results.filter((product) => product.category_name === newFilters.category);
    }
    if (newFilters.rating) {
      results = results.filter((product) => product.rating && product.rating >= newFilters.rating);
    }
    if (newFilters.gender) {
      results = results.filter((product) => product.gender_name === newFilters.gender);
    }
    if (newFilters.size) {
      results = results.filter((product) => product.sizes.includes(newFilters.size));
    }

    // If there’s a search query, apply it after filtering
    if (query) {
      results = results.filter(
        (product) =>
          product.name.toLowerCase().includes(query.toLowerCase()) ||
          (product.description && product.description.toLowerCase().includes(query.toLowerCase()))
      );
    }

    setFilteredProducts(results); // Update the filtered results
  };

  return (
    <div className="app-container">
      <h1 className="app-title">Shop</h1>
      <SearchBar onSearch={handleSearch} />
      <Filters filters={filters} onApplyFilters={applyFilters} />
      <ProductList products={filteredProducts} />
    </div>
  );
}

export default App;
